import React from 'react';
import './App.css';
import ReviewItems from './Reviews';

function App() {
  return (
    <div className="Parentbox">
      <FotoProduk />
      <ProdukInfo
        isDiscount="yes"
        Cate="Lebaran"
        Price="200000"
        Title="Baju Koko Motif Bordir Abu - Dua Kantong - Putih"
        Info="Regular Fit  Warna Putih Kemeja dengan Koko Bahan Katun Motif Bordir Biru Pada Placket, Dua kantong nyama dipakai di manapun kapanpun. tampil lebih gaya dan lebih religius. stok terbatas tunggu apa lagi segera beli keburu habis."
      />
      <ReviewItems />
    </div>
  );
}

function FotoProduk() {
  return (
    <div className="Foto">
      <img src="baju.jpg" alt="baju" />
    </div>
  );
}

function Harga(props) {
  const { Price } = props;
  if (Price > 100000) {
    return (
      <div>
        <p><strike>IDR {Price}</strike></p>
        <p>IDR {Price - 0.1 * Price}</p>
      </div>

    );
  }
  else if (Price === 100000 && Price < 100000) {
    return (
      <div>
        <p><strike>IDR {Price}</strike></p>
        <p>IDR {Price - 0.05 * Price}</p>
      </div>
    );
  }
  else {
    return (
      <p><strike>IDR {Price}</strike></p>
    );
  }
}

function ProdukInfo(props) {
  let { Price } = props;
  const { Cate, Title, Info } = props;
  const benefits = ["Tidak kusut terkena air", "Bahan lebih halus", "Tidak gerah"];
  const listBenefits = benefits.map((itemBenefit) =>
    <li>{itemBenefit}</li>
  );
  return (
    <div className="Deskripsi">
      <p className="Cate">{Cate}</p>
      <h1 className="Title">{Title}</h1>

      <Harga Price={Price} />
      <p className="Info">{Info}</p>
      <ul>
        {listBenefits}
      </ul>
      <a onClick={(e) => TambahCart(Title, e)} href="#">Add to Cart</a>
    </div>
  );
}

function TambahCart(e) {
  return console.log("Membeli " + e);
}

export default App;
