import React from 'react';
import './App.css';

function Produks() {
    const barang = [
        {
            "id": 1,
            "foto": "https://images.pexels.com/photos/2232981/pexels-photo-2232981.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500",
            "cate": "Lebaran",
            "price": "300000",
            "title": "Baju Koko Motif Bordir Abu - Dua Kantong - Putih",
            "info": "Regular Fit  Warna Putih Kemeja dengan Koko Bahan Katun Motif Bordir Biru Pada Placket, Dua kantong nyama dipakai di manapun kapanpun. tampil lebih gaya dan lebih religius. stok terbatas tunggu apa lagi segera beli keburu habis."
        },
        {
            "id": 2,
            "foto": "https://images.pexels.com/photos/764529/pexels-photo-764529.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500",
            "cate": "Lebaran",
            "price": "300000",
            "title": "Baju Koko Motif Bordir Abu - Dua Kantong - Putih",
            "info": "Regular Fit  Warna Putih Kemeja dengan Koko Bahan Katun Motif Bordir Biru Pada Placket, Dua kantong nyama dipakai di manapun kapanpun. tampil lebih gaya dan lebih religius. stok terbatas tunggu apa lagi segera beli keburu habis."
        },
        {
            "id": 3,
            "foto": "https://images.pexels.com/photos/2100063/pexels-photo-2100063.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
            "cate": "Lebaran",
            "price": "300000",
            "title": "Baju Koko Motif Bordir Abu - Dua Kantong - Putih",
            "info": "Regular Fit  Warna Putih Kemeja dengan Koko Bahan Katun Motif Bordir Biru Pada Placket, Dua kantong nyama dipakai di manapun kapanpun. tampil lebih gaya dan lebih religius. stok terbatas tunggu apa lagi segera beli keburu habis."
        },
    ];

    const listProduk = barang.map((itemProduk) =>
        <div>
            <div className="Foto"><img src={itemProduk.foto} alt="foto" /></div>
            <p className="Cate">{itemProduk.cate}</p>
            <h1 className="Title">{itemProduk.title}</h1>
            <p className="Price">{itemProduk.price}</p>
            <p className="Info">{itemProduk.info}</p>
        </div>
    );
    return (
        <div className="Deskripsi">
            {listProduk}
            <a onClick={(e) => TambahCart(listProduk.itemProduk.title, e)} href="#">Add to Cart</a>
        </div>
    );
}

function TambahCart(e) {
    return console.log("Membeli " + e);
}

export default Produks;
