import React from 'react';
import './App.css';

function App() {
  return (
    <div className="Parentbox">
      <FotoProduk />
      <ProdukInfo
        isDiscount="yes"
        Cate="Lebaran"
        Title="Baju Koko Motif Bordir Abu - Dua Kantong - Putih"
        Price="IDR 243,950"
        Info="Regular Fit  Warna Putih Kemeja dengan Koko Bahan Katun Motif Bordir Biru Pada Placket, Dua kantong nyama dipakai di manapun kapanpun. tampil lebih gaya dan lebih religius. stok terbatas tunggu apa lagi segera beli keburu habis."
      />
    </div>
  );
}

function FotoProduk() {
  return (
    <div className="Foto">
      <img src="baju.jpg" alt="baju" />
    </div>
  );
}

function CheckDiscount(props) {
  const { isDiscount } = props;
  if (isDiscount == "yes") {
    return (
      <p>Diskon 50% Off</p>
    );
  }
  else if (isDiscount == "coming") {
    return (
      <p>Akan ada diskon ...</p>
    );
  }
  else {
    return (
      <p>Belum ada diskon</p>
    );
  }
}

function ProdukInfo(props) {
  const { Cate, Title, Price, Info, isDiscount } = props;
  return (
    <div className="Deskripsi">
      <p className="Cate">{Cate}</p>
      <h1 className="Title">{Title}</h1>
      <p className="Price">{Price}</p>
      <p className="Info">{Info}
        <CheckDiscount isDiscount={isDiscount} />
      </p>
      <a onClick={(e) => TambahCart(Title, e)} href="#">Add to Cart</a>
    </div>
  );
}

function TambahCart(e) {
  return console.log("Membeli " + e);
}

export default App;
